import { OkPacket } from 'mysql2/typings/mysql/lib/protocol/packets/OkPacket';
import { ResultSetHeader } from 'mysql2/typings/mysql/lib/protocol/packets/ResultSetHeader';
import { RowDataPacket } from 'mysql2/typings/mysql/lib/protocol/packets/RowDataPacket';

declare type ProcedureCallPacket<
  T = RowDataPacket[] | RowDataPacket[][] | ResultSetHeader
> = T extends RowDataPacket[]
  ? [...T, ResultSetHeader]
  : T extends RowDataPacket[][]
  ? [...T, ResultSetHeader]
  : T extends ResultSetHeader | OkPacket | OkPacket[]
  ? ResultSetHeader
  :
      | [...RowDataPacket[], ResultSetHeader]
      | [...RowDataPacket[][], ResultSetHeader]
      | ResultSetHeader;

export { ProcedureCallPacket };
